function getValidation(response){

	if(response === "true" || response === true){
		response = true;
	}else{
		response = false;
	}

	if(response){
		g_form.clearMessages();
        var infomsg = "Valid Charge Code";
		g_form.showFieldMsg('u_bac_charge_code', infomsg, 'info', false);
	}else{ 
		g_form.clearMessages();
		var errormsg = 'Invalid Charge Code';
		g_form.showFieldMsg('u_bac_charge_code',errormsg,'warning',false);

	}
}

