var loadingDialog;

function showLoading() {
    loadingDialog.render();
}

function hideLoading() {
    loadingDialog.destroy();
}

function onChange(control, oldValue, newValue, isLoading) {
	var requestedForUserName = '';

	function runValidation() {
		// stop the check since the user can't see the development_environment field
		if (!g_user.hasRole("sn_app_eng_notify.app_engine_admin")) {
			hideLoading();

			return;
		}

// 		check to see if the selected environment has the user and the right version of AES installed
		var validateAesVersion = new GlideAjax('sn_app_intake.ValidateIntakeStatus');
		validateAesVersion.addParam('sysparm_name', 'validate');
		validateAesVersion.addParam('sysparm_dev_environment_sys_id', newValue);
		validateAesVersion.addParam('sysparm_requested_for_user_name', requestedForUserName);

		validateAesVersion.getXML(validate);
	}

	g_form.getReference('request.requested_for', function (caller) { requestedForUserName = caller.user_name; });

	loadingDialog = new GlideModal("sn_app_intake_checking_environment_aes_version", true);
	loadingDialog.setTitle('Check Intake Status');
	loadingDialog.on('bodyrendered', runValidation);

	showLoading();
}

function validate(response) {
    hideLoading();

    try {
        var answer = JSON.parse(response.responseXML.documentElement.getAttribute('answer'));

        if (answer.hasWarning)
            g_form.showFieldMsg('development_instance', answer.warningMessage, 'warning');

    } catch (e) {
       jslog(e.message);
    }
}

/* global AssetReclamation */

function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }

	var today = new Date();
	today.setHours(0,0,0,0);
	var startDateStr = g_form.getValue('reclaim_date');
	var startDateNum = getDateFromFormat(startDateStr, g_user_date_format);

	if (startDateNum < today) {
		g_form.showFieldMsg('reclaim_date', getMessage('Reclaim date cannot be in the past'), 'error');
	}
}

function onSubmit() {
	var today = new Date();
	today.setHours(0,0,0,0);
	var startDateStr = g_form.getValue('reclaim_date');
	var startDateNum = getDateFromFormat(startDateStr, g_user_date_format);

	if (startDateNum < today) {
		return false;
	}

	return true;
}

function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }

	if (g_form.getValue('employee_separation') === 'true' && newValue != '') {
		var assetsGr = new GlideAjax("global.AssetReclamationUtil");
		assetsGr.addParam('sysparm_name', 'getAssetsAssignedTo');
		assetsGr.addParam('sysparm_userVal', g_form.getValue('requested_for'));
		assetsGr.getXML(callbackFn);

		function callbackFn(response) {
			g_form.setValue('assets', response.responseXML.documentElement.getAttribute("answer").split(','));
			g_form.setReadOnly('assets');
		}
	} else {
		g_form.clearValue('assets');
	}
}

