	}
}

function onChange(control, oldValue, newValue, isLoading) {
    var mode = localStorage.getItem('sc_service_fulfillment_step_mode');
    var sf_action = localStorage.getItem('sc_service_fulfillment_action');
    var sf_prev_stage = localStorage.getItem('sc_service_fulfillment_previous_stage');
    var sf_current_stage = localStorage.getItem('sc_service_fulfillment_current_stage');
    var isGrouped = localStorage.getItem('is_sc_service_fulfillment_step_grouped') == 'true';
    if (isLoading) {
        if (mode == "edit") {
            if (isGrouped) {
                g_form.setValue('sequence', 'with_previous');
            }
            g_form.setValue('service_fulfillment_stage', sf_current_stage);
            g_form.setReadOnly('service_fulfillment_stage', true);
            g_form.setReadOnly('service_fulfillment_step_configuration', true);
        } else {
            if (sf_action)
                g_form.setValue('service_fulfillment_step_configuration', sf_action);
            // Asuming default value of sequence is after previous, so create a new stage
            g_form.setValue('service_fulfillment_stage', '');
        }
        // If no previous stage, disable sequence
		g_form.setReadOnly('sequence', !sf_prev_stage);
        return;
    }

    if (mode == 'edit') {
        if (isGrouped) {
            if ('with_previous' == newValue)
                g_form.setValue('service_fulfillment_stage', sf_current_stage);
            else
                g_form.setValue('service_fulfillment_stage', '');
        } else {
            if ('with_previous' == newValue)
                g_form.setValue('service_fulfillment_stage', sf_prev_stage);
            else
                g_form.setValue('service_fulfillment_stage', sf_current_stage);
        }
    } else {
        if ('with_previous' == newValue)
            g_form.setValue('service_fulfillment_stage', sf_prev_stage);
        else
            g_form.setValue('service_fulfillment_stage', '');

    }

}

function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading) {
        g_form.setDisplay('delivery_time', false);
        convertDurationToDays();
        return;
    }
    var dateSuffix = ' 00:00:00';
    var daysString = !newValue ? '0' : newValue;
    g_form.setValue('delivery_time', daysString + dateSuffix);
}

function convertDurationToDays() {
    var durationValue = g_form.getValue('delivery_time');
    var days = (!durationValue || durationValue.split(" ").length == 1) ? 0 : durationValue.split(" ")[0];
    g_form.setValue('delivery_time_var', days);
}

function onChange(control, oldValue, newValue, isLoading) {
    var isTemplateMode = g_form.$private.getField('sysparm_template_name');
    if (isTemplateMode) {
        g_form.setDisplay('fulfillment_steps', false);
        g_form.setDisplay('delivery_time_var', false);
        g_form.clearValue('fulfillment_steps');
        return;
    }

    if (newValue == '') {
        g_form.setDisplay('fulfillment_steps', false);
        g_form.setDisplay('delivery_time_var', false);
        return;
    }

    var ERROR_MESSAGE_VALIDATION_FAILED = getMessage('Error while fetching the SC flow registry data');
    var url = 'api/now/table/sc_service_fulfillment_flow_registry?sysparm_query=active=true^service_fulfillment_flow=' + g_form.getValue('flow_designer_flow');

    glideRequest.get(url, {}).then(function(response) {
        if (!response.ok)
            throw new Error(ERROR_MESSAGE_VALIDATION_FAILED);

        return response.json();
    }).then(function(data) {
        var result = data.result;
        g_form.setDisplay('fulfillment_steps', result.length > 0);
        g_form.setDisplay('delivery_time_var', result.length > 0);
    }, errorHandler);


    function errorHandler() {
        g_form.setDisplay('fulfillment_steps', false);
        g_form.setDisplay('delivery_time_var', false);
        g_form.addErrorMessage(ERROR_MESSAGE_VALIDATION_FAILED);
    }
}

function onSubmit() {
    g_ui_scripts.getUIScript('CalculateTotalCostWS').then(function(myUIScript) {
		g_form.clearMessages();

		var origEndDate = localStorage.getItem("endDate");
		var newEndDate = g_form.getValue('end_date');
		var userDateFormat = localStorage.getItem("userDateFormat");
		var endDateFormat = userDateFormat;
		if(origEndDate === newEndDate) {
			endDateFormat = 'yyyy-MM-dd';
		}

		var decimalRegex = /^\d*(\.\d{1,2})?$/;
		var startDate = myUIScript.getFormattedDate(g_form.getValue('start_date'), userDateFormat);
        var endDate = myUIScript.getFormattedDate(g_form.getValue('end_date'), endDateFormat);
        var minDate = myUIScript.getFormattedDate(g_form.getValue('min_date'), 'yyyy-MM-dd');
        var maxDate = myUIScript.getFormattedDate(g_form.getValue('max_date'), 'yyyy-MM-dd');

		var validationPassed = decimalRegex.test(g_form.getValue('unit_cost'))
			&& startDate <= endDate
			&& (!minDate || startDate >= minDate)
			&& (!maxDate || endDate <= maxDate);

		if (!validationPassed) {
			g_form.addErrorMessage(getMessage('Please correct the form errors below.'));
		}

		return validationPassed;
	});
}

function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }

	var decimalRegex = /^\d*(\.\d{1,2})?$/;
	g_form.hideFieldMsg('unit_cost', true);

	if (!decimalRegex.test(g_form.getValue('unit_cost'))) {
		g_form.showFieldMsg('unit_cost', 'Unit cost must be a numeric or decimal value.', 'error');
	}
}

function onChange(control, oldValue, newValue, isLoading) {
	// NOTE: Due to limitations of Record Producer, most of the code here is duplicated with EndDate onChange.
	//       Make sure any changes made in the duplicated section also are changed in the other file.

	// BEGIN DUPLICATED SECTION
	if (isLoading) {
		return;
	}

	g_ui_scripts.getUIScript('CalculateTotalCostWS').then(function(myUIScript) {
		g_form.hideFieldMsg('start_date', true);
		g_form.hideFieldMsg('end_date', true);

		var origEndDate = localStorage.getItem("endDate");
		var newEndDate = g_form.getValue('end_date');
		var userDateFormat = localStorage.getItem("userDateFormat");
		var endDateFormat = userDateFormat;
		if(origEndDate === newEndDate) {
			endDateFormat = 'yyyy-MM-dd';
		}
		var hasStartDate = g_form.getValue('start_date') != '';
		var hasEndDate = g_form.getValue('end_date') != '';
		var startDate = myUIScript.getFormattedDate(g_form.getValue('start_date'), userDateFormat);
        var endDate = myUIScript.getFormattedDate(g_form.getValue('end_date'),endDateFormat);
        var minDate = myUIScript.getFormattedDate(g_form.getValue('min_date'),'yyyy-MM-dd');
        var maxDate = myUIScript.getFormattedDate(g_form.getValue('max_date'),'yyyy-MM-dd');

		var message;
		if (startDate && minDate && startDate < minDate) {
			message = getMessage('The start date must be on or after the source entitlement start date: {0}')
				.replace('{0}', g_form.getValue('min_date'));
			g_form.showFieldMsg('start_date', message, 'error');
		}

		if (endDate && maxDate && endDate > maxDate) {
			message = getMessage('The end date must be on or before the source entitlement end date: {0}')
				.replace('{0}', g_form.getValue('max_date'));
			g_form.showFieldMsg('end_date', message, 'error');
		}
		// END DUPLICATED SECTION

		// BEGIN UNIQUE SECTION
		if (startDate && endDate && startDate > endDate) {
			g_form.showFieldMsg('start_date', getMessage('The start date must be less than the end date.'), 'error');
		}
		// END UNIQUE SECTION
	});
}

