function onLoad() {

	g_form.setDisplay('license_metric', false);
	g_form.setDisplay('metric_group', false);
	g_form.setDisplay('min_date', false);
	g_form.setDisplay('max_date', false);

		 var endDate =localStorage.getItem("endDate")
         var unitCost = localStorage.getItem("unitCost");
         var softwareModel = localStorage.getItem("softwareModel");
         var sourceEntitlement = localStorage.getItem("sysId")
         var licenseMetric = localStorage.getItem("licenseMetric");
         var metricGroup = localStorage.getItem("metricGroup");
         var minDate =  localStorage.getItem("minDate");
         var maxDate = localStorage.getItem("maxDate");

    if (minDate) {
        g_form.setValue('min_date', minDate);
    }

    if (maxDate) {
        g_form.setValue('max_date', maxDate);
    }

    if (endDate) {
        g_form.setValue('end_date', endDate);
    }
    if (unitCost) {
        g_form.setValue('unit_cost', parseFloat(unitCost));
    }
    g_form.setValue('software_model', softwareModel);
    g_form.setReadOnly('software_model', true);
    g_form.setValue('source_entitlement', sourceEntitlement);
    g_form.setReadOnly('source_entitlement', true);
    g_form.setValue('license_metric', licenseMetric);
    g_form.setValue('metric_group', metricGroup);

}

