var sessionDateFormat = function (dateString) {
	return formatDate(new Date(getDateFromFormat(dateString, 'yyyy-MM-dd')), g_user_date_format)
};

function onLoad() {
	g_form.setDisplay('license_metric', false);
	g_form.setDisplay('metric_group', false);
	g_form.setDisplay('min_date', false);
	g_form.setDisplay('max_date', false);

	var url = new GlideURL();
	url.setFromCurrent();

	var endDate = url.getParam('sysparm_endDate');
	var unitCost = url.getParam('sysparm_unitCost');
	var softwareModel = url.getParam('sysparm_softwareModel');
	var sourceEntitlement = url.getParam('sysparm_sourceEntitlement');
	var licenseMetric = url.getParam('sysparm_licenseMetric');
	var metricGroup = url.getParam('sysparm_metricGroup');
	var minDate = url.getParam('sysparm_minDate');
	var maxDate = url.getParam('sysparm_maxDate');

	if (minDate) {
		g_form.setValue('min_date', sessionDateFormat(minDate));
	}

	if (maxDate) {
		g_form.setValue('max_date', sessionDateFormat(maxDate));
	}

	if (endDate) {
		g_form.setValue('end_date', sessionDateFormat(endDate));
	}
	if (unitCost) {
		g_form.setValue('unit_cost', parseFloat(unitCost));
	}
	g_form.setValue('software_model', softwareModel);
	g_form.setReadOnly('software_model', true);
	g_form.setValue('source_entitlement', sourceEntitlement);
	g_form.setReadOnly('source_entitlement', true);
	g_form.setValue('license_metric', licenseMetric);
	g_form.setValue('metric_group', metricGroup);
}

function onChange(control, oldValue, newValue, isLoading) {
	// NOTE: Due to limitations of Record Producer, most of the code here is duplicated with EndDate onChange.
	//       Make sure any changes made in the duplicated section also are changed in the other file.

	// BEGIN DUPLICATED SECTION
	if (isLoading) {
		return;
	}

	g_form.hideFieldMsg('start_date', true);
	g_form.hideFieldMsg('end_date', true);

	var hasStartDate = g_form.getValue('start_date') != '';
	var hasEndDate = g_form.getValue('end_date') != '';
	var startDate = getDateFromFormat(g_form.getValue('start_date'), g_user_date_format);
	var endDate = getDateFromFormat(g_form.getValue('end_date'), g_user_date_format);
	var minDate = getDateFromFormat(g_form.getValue('min_date'), g_user_date_format);
	var maxDate = getDateFromFormat(g_form.getValue('max_date'), g_user_date_format);

	if (hasStartDate && !startDate) {
		g_form.showFieldMsg('start_date', new GwtMessage().getMessage("Invalid start date. Expecting format '{0}'", g_user_date_format), 'error');
	} 

	if (hasEndDate && !endDate) {
		g_form.showFieldMsg('end_date', new GwtMessage().getMessage("Invalid start date. Expecting format '{0}'", g_user_date_format), 'error');
	}

	if (startDate && minDate && startDate < minDate) {
		g_form.showFieldMsg('start_date', new GwtMessage().getMessage('The start date must be on or after the source entitlement start date: {0}', g_form.getValue('min_date')), 'error');
	}

	if (endDate && maxDate && endDate > maxDate) {
		g_form.showFieldMsg('end_date', new GwtMessage().getMessage('The end date must be on or before the source entitlement end date: {0}', g_form.getValue('max_date')), 'error');
	}
	// END DUPLICATED SECTION

	// BEGIN UNIQUE SECTION
	if (startDate && endDate && startDate > endDate) {
		g_form.showFieldMsg('start_date', getMessage('The start date must be less than the end date.'), 'error');
	}
	// END UNIQUE SECTION
}

