	} 

	if (hasEndDate && !endDate) {
		g_form.showFieldMsg('end_date', new GwtMessage().getMessage("Invalid start date. Expecting format '{0}'", g_user_date_format), 'error');
	}

	if (startDate && minDate && startDate < minDate) {
		g_form.showFieldMsg('start_date', new GwtMessage().getMessage('The start date must be on or after the source entitlement start date: {0}', g_form.getValue('min_date')), 'error');
	}

	if (endDate && maxDate && endDate > maxDate) {
		g_form.showFieldMsg('end_date', new GwtMessage().getMessage('The end date must be on or before the source entitlement end date: {0}', g_form.getValue('max_date')), 'error');
	}
	// END DUPLICATED SECTION

	// BEGIN UNIQUE SECTION
	if (startDate && endDate && startDate > endDate) {
		g_form.showFieldMsg('end_date', getMessage('The end date must be greater than the start date.'), 'error');
	}
	// END UNIQUE SECTION
}

function onSubmit() {
	g_form.clearMessages();

	var decimalRegex = /^\d*(\.\d{1,2})?$/;
	var startDate = getDateFromFormat(g_form.getValue('start_date'), g_user_date_format);
	var endDate = getDateFromFormat(g_form.getValue('end_date'), g_user_date_format);
	var minDate = getDateFromFormat(g_form.getValue('min_date'), g_user_date_format);
	var maxDate = getDateFromFormat(g_form.getValue('max_date'), g_user_date_format);

	var validationPassed = decimalRegex.test(g_form.getValue('unit_cost'))
		&& startDate > 0
		&& endDate > 0
		&& startDate <= endDate
		&& (!minDate || startDate >= minDate)
		&& (!maxDate || endDate <= maxDate);

	if (!validationPassed) {
		g_form.addErrorMessage(getMessage('Please correct the form errors below.'));
	}

	return validationPassed;
}

function onLoad() {
	//Type appropriate comment here, and begin script below
	var chgReqId = getParmVal('sysparm_chgReqId');
	if(chgReqId){
		g_form.setValue("variables.created_from_change", chgReqId);

		var itemGUID = gel('sysparm_item_guid');

		// Noticed that during the RP flow, a unique id is generated for the record ('std_change_proposal' in our case) which will be generated
		// by the record producer. Copying all the attachments from change_request to this record.
		var copyAttAj = new GlideAjax('StdChangeUtils');
		copyAttAj.addParam('sysparm_name', 'copyAttachments');
		copyAttAj.addParam('sysparm_srcTable', 'change_request');
		copyAttAj.addParam('sysparm_srcSysId', chgReqId);
		copyAttAj.addParam('sysparm_targetTable', 'std_change_proposal');
		copyAttAj.addParam('sysparm_targetSysId', itemGUID.value);
		copyAttAj.getXML(processAttachments);
	}
}

function getParmVal(name){
	var url = document.URL.parseQuery();
	if(url[name]){
		return decodeURI(url[name]);
	}
	else{
		return;
	}
}

