function onChange(control, oldValue, newValue, isLoading) {
  var sourceOptions = gel('delegate_roles_roles_select_1').options;
  // Remove all options from the Selected list
  var selectedIds = new Array();
  var index = 0;
  for (var i = 0; i < sourceOptions.length; i++) {
    if (sourceOptions[i].value != "--None--") // don't move the empty label
      selectedIds[index++] = i;
  }
  if (selectedIds.length > 0) {
    var s0 = gel('delegate_roles_roles_select_0');
    moveSelectedOptions(selectedIds, gel('delegate_roles_roles_select_1'), s0, '--None--', null, '--None--');
    sortSelect(s0);
    s0.selectedIndex = -1;
  }

  if (newValue != "") {
    var ga = new GlideAjax('DelegateRolesAjax');
    ga.addParam('sysparm_name','getDelegateUserRoles');
    ga.addParam('sysparm_user', g_form.getValue("delegate_roles_user"));
    ga.addParam('sysparm_group', g_form.getValue("delegate_roles_group"));
    ga.getXML(populateDelegatedRoles);
    g_form.setDisplay("delegate_roles_roles", true);
} else
    g_form.setDisplay("delegate_roles_roles", false);
}

function onLoad() {
   g_form.setDisplay("delegate_roles_not_selected", false);
   g_form.setDisplay("delegate_roles_roles", false);
   g_form.setDisplay("delegate_roles_user", false);
}

function populateDelegatedRoles(response) {
  var selectedIDs = new Array();
  var index = 0;
  var answer = response.responseXML.documentElement.getAttribute("answer").split(",");
  for (var i = 0; i < answer.length; i++) {
    var roleID = answer[i];
    var options = gel("delegate_roles_roles_select_0").options;
    for (var n = 0; n < options.length; n++) {
      if (options[n].value == roleID) {
        selectedIDs[index++] = n;
        break;
      }
    }
  }
  selectedIDs = selectedIDs.sort();
  if (selectedIDs.length > 0) { // user already has roles in this group, so show them as selected
    var s1 = gel('delegate_roles_roles_select_1');
    moveSelectedOptions(selectedIDs, gel('delegate_roles_roles_select_0'), s1, '--None--', null, '--None--');
    sortSelect(s1);
    s1.selectedIndex = -1;
  }
}

function onSubmit() {
   var unselected = new Array();
   var options = gel("delegate_roles_roles_select_0").options;
   for (var i = 0; i < options.length; i++)
      unselected.push(options[i].value);
   g_form.setValue("delegate_roles_not_selected", unselected.join());

}

